mapper.select:
SELECT io.*, u.username, u.phonenum, io.supplier_id as supplierId, su.supplier as supplierName, io.finish_time as finishTime, COALESCE ( SUM( CASE WHEN f.type = '收入' THEN f.total_price ELSE 0 END ), 0 ) AS total_in_account, (COALESCE ( SUM( CASE WHEN f.type = '出库' THEN f.total_price ELSE 0 END ), 0 ) + COALESCE ( -SUM( CASE WHEN f.type = '支出' THEN f.total_price ELSE 0 END ), 0 )) AS total_out_account FROM `jsh_in_out_item` io LEFT JOIN `jsh_user` u ON io.manager = u.id LEFT JOIN view_project_account_flow as f on f.id = io.id left join jsh_supplier su on su.id = io.supplier_id where 1=1
and io.name like #{bindName}
and io.type=#{type}
and io.remark like #{bindRemark}
and ifnull(io.delete_flag,'0') !='1' GROUP BY io.id order by io.sort asc, io.id desc
limit #{offset},#{rows}

SELECT
COUNT(id)
FROM jsh_in_out_item
WHERE 1=1


    and name like #{bindName}


    and type=#{type}



    and remark like #{bindRemark}

and ifnull(delete_flag,'0') !='1'

mapper.update:
update jsh_in_out_item
set delete_flag='1'
